export default function TopicContent({ topik, onStartTest, onViewScore, hasAttempted }: TopicContentProps) {
    const embedUrl = getYouTubeEmbedUrl(topik?.materi?.youtube);
    const subMateris = topik?.materi?.subMateris || [];

    const [fontClass, setFontClass] = useState('font-poppins');
    const [proseSizeClass, setProseSizeClass] = useState('prose-base');

    useEffect(() => {
        const loadSettings = () => {
            const storedFontSize = localStorage.getItem('materiFontSize');
            const storedFontStyle = localStorage.getItem('materiFontStyle');

            if (storedFontStyle) {
                setFontClass(storedFontStyle);
            }

            if (storedFontSize) {
                switch (storedFontSize) {
                    case '14px': setProseSizeClass('prose-sm'); break;
                    case '16px': setProseSizeClass('prose-base'); break;
                    case '18px': setProseSizeClass('prose-lg'); break;
                    case '20px': setProseSizeClass('prose-xl'); break;
                    case '24px': setProseSizeClass('prose-2xl'); break;
                    default: setProseSizeClass('prose-base');
                }
            } else {
                // Default ke 14px (prose-sm) jika di mobile (< 768px) dan belum ada pengaturan
                if (window.innerWidth < 768) {
                    setProseSizeClass('prose-sm');
                } else {
                    setProseSizeClass('prose-base');
                }
            }
        };

        loadSettings();
        window.addEventListener('settings-updated', loadSettings);
        return () => window.removeEventListener('settings-updated', loadSettings);
    }, []);

    return (
        <div className="px-1 sm:px-10 pb-5 pt-4 border-t border-gray-200 dark:border-gray-700 max-h-[80vh] overflow-y-auto custom-pattern-bg">
            <style>{`
                .custom-pattern-bg {
                    background-color: #ffffff;
                    background-image: url("${patternLight}");
                }
                .dark .custom-pattern-bg {
                    background-color: transparent;
                    background-image: url("${patternDark}");
                }
            `}</style>
            {embedUrl && (
                <div className="aspect-video mb-6 rounded-lg overflow-hidden w-76 sm:w-2xl max-w-96 sm:max-w-full mx-auto">
                    <iframe src={embedUrl} title={`Video untuk ${topik.title}`} allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen className="w-full h-full"></iframe>
                </div>
            )}

            <div className={`space-y-4 ${fontClass}`}>
                {subMateris.length > 0 ? (
                    subMateris.map((sub, index) => (
                        <div
                        key={sub._id || index}
                        id={sub._id} // ID unik untuk target scroll
                        className="max-w-full w-full scroll-mt-24 bg-white dark:bg-gray-800 p-5 rounded-xl border border-slate-300 dark:border-gray-700/60 shadow-sm border-l-7 border-l-blue-400"
                        >
                            <h4 className={`font-bold ${proseSizeClass} !mb-2 text-gray-800 dark:text-gray-100`}>{sub.title}</h4>
                            <div className={`prose pre max-w-none ${proseSizeClass} [&_p]:mb-3`} dangerouslySetInnerHTML={{ __html: sub.content }} />
                        </div>
                    ))
                ) : (
                    <p>Materi untuk topik ini belum tersedia.</p>
                )}
            </div>

            <div id={`uji-pemahaman-${topik._id}`} className="mt-5 scroll-mt-24">
                <div className="border border-slate-300 dark:border-gray-700 relative overflow-hidden bg-gradient-to-br from-slate-50 to-slate-100 dark:from-gray-900 dark:to-gray-800 rounded-2xl p-5 sm:p-6 flex flex-col gap-5 shadow-sm transition-all duration-300 hover:shadow-lg hover:-translate-y-1">

                    {/* Decorative Bubbles */}
                    {/* <div className="absolute -top-12 -right-12 w-40 h-40 bg-blue-300 dark:bg-blue-400/5 rounded-full blur-3xl pointer-events-none"></div>
                    <div className="absolute -bottom-12 -left-12 w-40 h-40 bg-purple-300 dark:bg-purple-400/5 rounded-full blur-3xl pointer-events-none"></div> */}

                    {/* Gambar + Deskripsi sejajar */}
                    <div className="relative z-10 flex flex-row items-center gap-4 ">
                        <img
                            src="/post-test-topik.webp"
                            alt="Post Test"
                            width={256}
                            height={256}
                            className="w-24 h-24 sm:w-28 sm:h-28 object-contain flex-shrink-0"
                        />
                        <div className="text-left flex-1 border-slate-300 ">
                            <h4 className="text-base sm:text-xl font-bold text-gray-800 dark:text-gray-100">
                                Waktunya Uji Pemahaman!
                            </h4>
                            <p className="text-xs sm:text-sm text-gray-600 dark:text-gray-400 mt-1 leading-relaxed">
                                Setelah mempelajari materi, uji pemahamanmu dengan mengerjakan tes singkat ini.
                            </p>
                        </div>
                    </div>

                    {/* Tombol sejajar */}
                    <div className="relative z-10 flex flex-row justify-start gap-3 w-full flex-wrap">
                        <button
                            onClick={() => onStartTest(topik, hasAttempted)}
                            className="text-sm px-4 py-2 bg-yellow-400 hover:bg-yellow-300 text-black font-semibold rounded-xl shadow-md transition transform hover:scale-105 active:scale-95 focus:ring-2 focus:ring-yellow-200 sm:px-5 sm:py-2"
                        >
                            {hasAttempted ? "Ulangi Uji Pemahaman" : "Mulai Uji Pemahaman"}
                        </button>

                        {hasAttempted && (
                            <button
                                onClick={() => onViewScore(topik)}
                                className="text-sm px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white font-semibold rounded-xl shadow-md transition transform hover:scale-105 active:scale-95 focus:ring-2 focus:ring-blue-300 sm:px-5 sm:py-2"
                            >
                                Lihat Skor
                            </button>
                        )}
                    </div>
                </div>
            </div>

        </div>
    );
}